    //   }
    // }
    }


    // const handleSubmit = async (event) => {
    //   event.preventDefault();

    //   if (selectedFile) {
    //     const formData = new FormData();
    //     formData.append('file', selectedFile);
    //   }
    // };

    return (
      <div>
        <h2>File Upload</h2>
        <form>
          <input
            type="file"
            accept=".csv, .xlsx, .pdf"
            onChange={handleFileChange}
          />
          <button type="submit" onClick={handleUpload}>
            Upload
          </button>
        </form>
      </div>
    );
}
