        //   } else {
        //     alert('File upload failed.');
        //   }
        // } catch (error) {
        //   console.error('Error uploading file:', error);
        //   alert('File upload failed.');
        // }
      }
    };

    return (
      <div>
        <h2>File Upload</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="file"
            accept=".csv, .xlsx, .pdf"
            onChange={handleFileChange}
          />
          <button type="submit" disabled={!selectedFile} onClick={handleUpload}>
            Upload File
          </button>
        </form>
      </div>
    );
}
